import React, { useState, useEffect } from 'react';

function Dashboard() {
  const [userData, setUserData] = useState({});
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    fetch('/dashboard')
      .then((response) => response.json())
      .then((data) => setUserData(data))
      .catch((error) => console.error('Error fetching user data:', error));
  }, []);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = () => {
    // Implement code to save the edited data on the server

    // Assuming you have a function to update user data, e.g., updateUserData(userData)
    // Call updateUserData(userData) to send the edited data to the server

    setIsEditing(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  return (
    <div>
      <h1>Welcome to Your Dashboard</h1>
      {isEditing ? (
        <div>
          <h2>Edit Your Information:</h2>
          <div>
            <label>First Name:</label>
            <input
              type="text"
              name="firstName"
              value={userData.firstName}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Last Name:</label>
            <input
              type="text"
              name="lastName"
              value={userData.lastName}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={userData.email}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Address 1:</label>
            <input
              type="text"
              name="address1"
              value={userData.address1}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Address 2:</label>
            <input
              type="text"
              name="address2"
              value={userData.address2}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Phone Number:</label>
            <input
              type="tel"
              name="number"
              value={userData.number}
              onChange={handleInputChange}
            />
          </div>
          <button onClick={handleSave}>Save</button>
        </div>
      ) : (
        <div>
          <h2>User Information:</h2>
          <p>First Name: {userData.firstName}</p>
          <p>Last Name: {userData.lastName}</p>
          <p>Email: {userData.email}</p>
          <p>Address 1: {userData.address1}</p>
          <p>Address 2: {userData.address2}</p>
          <p>Phone Number: {userData.number}</p>
          <button onClick={handleEdit}>Edit</button>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
