import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";

function Registration() {

    const navigate = useNavigate();
    const [passwordError, setPasswordError] = useState("");
    const [formData, setFormData] = useState({
        password: "",
        cpassword: "",
      });
      
    const handleSubmit = async (e) => {
        e.preventDefault();

        

        try {
            const response = await fetch('/registration', {
                method: 'POST',
                body: formData,
            });

            if (formData.password !== formData.cpassword) {
                setPasswordError("Passwords do not match");
            } else {
                // Passwords match, proceed with form submission
                setPasswordError(""); // Clear any previous error message

                navigate("\login");
            }

            if (response.ok) {
                // Registration was successful, you can redirect to a login page or show a success message
                console.log('Registration successful');
            } else {
                // Handle any registration errors
                console.error('Registration failed');
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    return <div className="card">
        <form className="main-block" method="POST" action="/registration" onSubmit={handleSubmit}>
            <div>
                <h1>Sign Up</h1>
            </div>
            <div className="name-block">
                <div>
                    <h2>First Name</h2>
                    <input type="text" name="firstName" placeholder="First name" pattern="^\S+$" required></input>
                </div>
                <div>
                    <h2>Last Name</h2>
                    <input type="text" name="lastName" placeholder="Last name" pattern="^\S+$" required></input>
                </div>
            </div>
            <div className="details-block">
                <div className="address-block">
                    <h2>Address</h2>
                    <input type="text" name="address1" placeholder="Address Line 1" required></input>
                    <input type="text" name="address2" placeholder="Address Line 2" required></input>
                    <input type="text" placeholder="Address Line 3" ></input>
                </div>
                <div>
                    <h2>Phone number</h2>
                    <input type="tel" name="number" placeholder="Phone number" pattern="^\S+$" required></input>
                </div>

            </div>
            <div className="personal">
                <div>
                    <h2>Username</h2>
                    <input type="email" name="email" placeholder="Username" pattern="^\S+$"></input>
                </div>
                <div className="Password">
                    <h2>Create Password:</h2>
                    <input
                        type="password"
                        name="password"
                        placeholder="create password"
                        pattern="^\S+$"
                        value={formData.password}
                        onChange={handleInputChange}
                    ></input>
                    <h2>Confirm Password:</h2>
                    <input
                        type="password"
                        name="cpassword"
                        placeholder="confirm password"
                        pattern="^\S+$"
                        value={formData.cpassword}
                        onChange={handleInputChange}
                    ></input>
                </div>

            </div>
            <div className="submit">
                <button type="submit">Sign up</button>
            </div>
        </form>
        <div>
            <p>Already registered?<Link to="/login">Login</Link></p>
        </div>
        <div id="password-error" className="error-message">
            {passwordError}
        </div>
    </div>;
}

export default Registration;
